document.querySelectorAll("div[mtd-img-selector]").forEach(n=>{const i=n.getAttribute("mtd-img-selector"),t=document.getElementById(`${i}-file-upload-result`),u=t.getAttribute("mtd-img-selector-text"),r=document.getElementById(`${i}-file-upload-input`),f=document.getElementById(`${i}-file-upload-cancel`);r.addEventListener("change",()=>{var i=r.value;/^\s*$/.test(i)?(n.classList.remove("active"),t.innerText=u):(n.classList.add("active"),t.innerText=i.replace("C:\\fakepath\\",""))});f.addEventListener("click",()=>{n.classList.remove("active"),t.innerText=u,r.value="",f.blur()})});